import "./App.css";
import React, { Component } from "react";
import ReactDOM from "react-dom";

import { GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import {
  MainLayout,
  PanelStyles,
  LocationName,
  AmenityName,
  AvailabilityTxt,
  PriceTxt,
  ScanButton,
  ScanButtonTxt,
  AmenitiesLayout,
  BottomNav,
  ScanBottomNavTxt,
  ScanButtonBottomNav,
  AccountIcon,
  HelpIcon,
  TopBanner,
} from "./styles/mapstyles";
